<?php if (!defined('THINK_PATH')) exit(); Header("Content-type: image/gif"); $border = 0; $how = 4; $w = $how*15+12; $h = 27; $fontsize = 15; $alpha = "1234567890abcdefghijkmnpqrstujklmnstuvwxyzABCDEFGHIJKMNPQRSTUJKLMNSTUVWXYZ"; $number = "vwxyzabcdefgh2345pqr6789"; $randcode2 = ""; srand((double)microtime()*1000000); $im = ImageCreate($w, $h); $bgcolor = ImageColorAllocate($im, 0xff, 0xff, 0xff); ImageFill($im, 0, 0, $bgcolor); if($border) { $black = ImageColorAllocate($im, 0xa9, 0xa9, 0xa9); ImageRectangle($im, 0, 0, $w-1, $h-1, $black); } for($i=0; $i<$how; $i++) { $alpha_or_number = mt_rand(0, 1); $str = $alpha_or_number ? $alpha : $number; $which = mt_rand(0, strlen($str)-1); $code = substr($str, $which, 1); $j = !$i ? 9 : $j+15; $color3 = ImageColorAllocate($im, mt_rand(0,50), mt_rand(0,50), mt_rand(0,50)); ImageChar($im, $fontsize, $j, 3, $code, $color3); $randcode2 .= $code; } for($i=0; $i<$how*30; $i++) { $color2 = ImageColorAllocate($im, mt_rand(0,255), mt_rand(0,255), mt_rand(0,255)); ImageSetPixel($im, mt_rand(0,$w), mt_rand(0,$h), $color2); } $_SESSION['randcode'] = $randcode2; imagegif($im); imagedestroy($im); ?>